function addAnimation(){scrollers.forEach(e=>{e.setAttribute("data-animated",!0);const r=e.querySelector(".scroller__inner"),t=Array.from(r.children);t.forEach(e=>{const t=e.cloneNode(!0);t.setAttribute("aria-hidden",!0),r.appendChild(t)})})}const scrollers=document.querySelectorAll(".scroller");window.matchMedia("(prefers-reduced-motion: reduce)").matches||addAnimation();